names.each do |name|
  u = User.new(name: name)
  u.email = name.downcase + rand(100..999).to_s + '@yopmail.com'
  u.age = rand(10..80)
  u.mobile_no = ('0'..'9').to_a.sample(10).join
  u.address = ('a'..'z').to_a.sample(30).join
  u.save
  puts "User Created: #{name}"
end

puts "All the users created of count: #{names.count}"

10.times do |a_i|
  text_length = article_text.length
  a = Article.new
  a.name = "Rspec #{a_i}"
  a.body = article_text[rand(text_length), 500] || 'Some text'
  a.user = User.all.sample
  a.save!
  puts "One articlr created, name: #{a.name}: #{a.id}"
  rand(10).times do |c_i|
    puts "Creating comments for article: #{a.name}"
    c = a.comments.new(body: article_text[rand(text_length), 50] || 'Some text')
    c.user = User.all.sample
    c.save!
  end
  
  puts "One articlr created, name: #{a.name} having comments: #{a.comments.count}"
end

